// src/components/ExpenseItem.js
import React, { useState } from 'react';
import ExpenseDate from './ExpenseDate';
import Card from './Card';
import './ExpenseItem.css';

function ExpenseItem(props) {
  const { id, title, amount, date, onUpdate, onDelete } = props;

  const updateExpenseHandler = () => {
    const updatedExpense = {
      title: "Updated Title", // обновите по необходимости
      amount: amount + 10, // пример изменения
      date: date
    };
    onUpdate(id, updatedExpense);
  };

  const deleteExpenseHandler = () => {
    onDelete(id);
  };

  return (
    <Card className="expense-item">
      <ExpenseDate date={date} />
      <div className="expense-item__description">
        <h2>{title}</h2>
        <div className="expense-item__price">${amount}</div>
      </div>
      <div>
        <button onClick={updateExpenseHandler}>Edit</button>
        <button onClick={deleteExpenseHandler}>Delete</button>
      </div>
    </Card>
  );
}

export default ExpenseItem;
