// src/firebase/firebase.js

import { initializeApp } from 'firebase/app';
import { getFirestore, collection, addDoc, getDocs, updateDoc, deleteDoc, doc } from 'firebase/firestore';

// Конфигурация Firebase (получена из консоли Firebase)
const firebaseConfig = {
    apiKey: "AIzaSyD2XnFgALF1ubzJ6c1uLJj9YMpvlD2Nom4",
    authDomain: "lab10-a744d.firebaseapp.com",
    projectId: "lab10-a744d",
    storageBucket: "lab10-a744d.firebasestorage.app",
    messagingSenderId: "949928075075",
    appId: "1:949928075075:web:527993a953d2f7427f59f6",
    measurementId: "G-JC5F574NT4"
  };

// Инициализация Firebase
const app = initializeApp(firebaseConfig);

// Инициализация Firestore
const db = getFirestore(app);

// Функции для работы с Firestore

// Добавить новую запись (витрату)
export const addExpense = async (expense) => {
  try {
    const docRef = await addDoc(collection(db, "expenses"), expense);
    console.log("Document written with ID: ", docRef.id);
  } catch (e) {
    console.error("Error adding document: ", e);
  }
};

export const getExpenses = async () => {
  const querySnapshot = await getDocs(collection(db, "expenses"));
  let expenses = [];
  querySnapshot.forEach((doc) => {
    const data = doc.data();
    // Преобразуем date в объект Date, если это строка
    const expenseDate = data.date ? new Date(data.date.seconds * 1000) : new Date(); 
    expenses.push({ id: doc.id, title: data.title, amount: data.amount, date: expenseDate });
  });
  return expenses;
};

// Обновить запись
export const updateExpense = async (id, updatedExpense) => {
  const expenseRef = doc(db, "expenses", id);
  await updateDoc(expenseRef, updatedExpense);
};

// Удалить запись
export const deleteExpense = async (id) => {
  const expenseRef = doc(db, "expenses", id);
  await deleteDoc(expenseRef);
};
